namespace Madkting\Connect\Block\System\Config\Form\Field;


use Madkting\Connect\Model\Config;
use Magento\Backend\Block\Template\Context;
use Magento\Config\Block\System\Config\Form\Field;

class GenericSyncBlock extends Field
{
    /**
     * @var Config
     */
    protected $config;
    /**
     * The value in second or unix of 24 hours
     * @const INT
     */
    const ONE_DAY = 86400;

    public function __construct(
        Config $config,
        Context $context, array $data = []
    )
    {
        $this->config = $config;
        parent::__construct($context, $data);
    }

    /**
     * Remove scope label
     *
     * @param  \Magento\Framework\Data\Form\Element\AbstractElement $element
     * @return string
     */
    public function render(\Magento\Framework\Data\Form\Element\AbstractElement $element)
    {
        $element->unsScope()->unsCanUseWebsiteValue()->unsCanUseDefaultValue();
        return parent::render($element);
    }

    /**
     * Return element html
     *
     * @param  \Magento\Framework\Data\Form\Element\AbstractElement $element
     * @return string
     *
     * @SuppressWarnings(PHPMD.UnusedFormalParameter)
     */
    protected function _getElementHtml(\Magento\Framework\Data\Form\Element\AbstractElement $element)
    {
        return $this->_toHtml();
    }

    /**
     * Validate if the last update was at least 24 hours ago
     * @return bool
     */
    protected function validateLastUpdate()
    {
        if ( (time() - $this->config->getLastSyncDate()) >= self::ONE_DAY ||
            $this->config->getLastSyncDate() === 0 ){
            return true;
        }

        return false;
    }

}