namespace Madkting\Connect\Model\Config\Source;

use Madkting\Connect\Helper\Data;
use Magento\Framework\Option\ArrayInterface;

class StatusMadkting implements ArrayInterface
{
    /**
     * @var Data
     */
    protected $madktingHelper;

    /**
     * StatusMadkting constructor
     *
     * @param Data $madktingHelper
     */
    public function __construct(
        Data $madktingHelper
    ) {
        $this->madktingHelper = $madktingHelper;
    }

    /**
     * Return array of options as value-label pairs
     *
     * @return array Format: array(array('value' => '<value>', 'label' => '<label>'), ...)
     */
    public function toOptionArray()
    {
        $options = [];

        $statuses = $this->madktingHelper->getStatuses();
        foreach ($statuses as $code => $label) {
            $options[] = ['value' => $code, 'label' => $label];
        }

        return $options;
    }
}
